mapper.update:

    UPDATE blog
    SET blogTitle=#{blogTitle}
        ,logoFile=#{logoFile}
         WHERE id=#{id}

mapper.insert:
     
        
           select seq_category_no.NEXTVAL from dual
        
           
                insert into category 
                values (#{cateNo}
                       ,#{id} 
	                   ,#{cateName}
	                   ,#{description}
	                   ,SYSDATE
	                    )

mapper.select:

    Select cateNo,id, cateName,description,regDate
    from category
    where cateNo=#{cateNo}


      Select postNo, postTitle,postContent,regDate
        from post
        where postNo=#{postNo}

mapper.insert:
     
        
           select seq_comments_no.NEXTVAL from dual
        
           
                insert into comments
                values (#{cmtNo}
                        ,#{postNo}
                        ,#{userNo}
                        ,#{cmtContent} 
	                    ,SYSDATE
	                    )

mapper.select:
 
        Select cmtNo, postNo,userNo,cmtContent,regDate
          from comments
          where postNo=#{cmtNo}
